function getItemById(n){var r,t,i,u;for(_id=n,r=$("#listFlowers button").length,t=1;t<=r;t++)i=document.getElementById(t),t!==n?i.className=i.className.replace(" active",""):i.className+=" active";u="api/GetById/"+n;fetch(u).then(n=>n.json()).then(n=>displayItem(n)).catch(n=>{console.error("Lỗi khi truy xuất thông tin từ server:",n),alert("Lỗi khi truy xuất thông tin từ server.")})}function displayItem(n){document.getElementById("tbxEnglishName").value=n.englishName;document.getElementById("tbxVietnameseName").value=n.vietnameseName;document.getElementById("imgThumbnail").src=n.thumbnail;document.getElementById("imgThumbnail").alt=n.vietnameseName;_thumbnail=n.thumbnail;n.richTextInfo!==null?_editor.setData(n.richTextInfo):_editor.setData("")}function submit(){const n=document.getElementById("tbxEnglishName"),t=document.getElementById("tbxVietnameseName"),i={ID:_id,EnglishName:n.value,VietnameseName:t.value,RichTextInfo:_editor.getData(),Thumbnail:_thumbnail};fetch("api/update",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(i)}).then().then(()=>{alert("Cập nhật thành công! Trang web sẽ tải lại...")||window.location.reload()}).catch(n=>{console.error("Lỗi cập nhật:",n),alert("Lỗi cập nhật thông tin lên server.")})}let _id=0;$(document).ready(function(){getItemById(1)});$("form").on("submit",submit);