function getItemById(n){var t="api/GetById/"+n;fetch(t).then(n=>n.json()).then(n=>displayItem(n)).catch(n=>console.error("Unable to get item:",n))}function displayItem(n){document.getElementById("tbxEnglishName").value=n.englishName;document.getElementById("tbxVietnameseName").value=n.vietnameseName;document.getElementById("rtbInfo").innerHTML=n.richTextInfo;document.getElementById("imgThumbnail").src=n.thumbnail}